bool is_union(const char *t)
{
    char *tm = (char*)t;
    size_t tmlen;
    if (strncmp(tm, "const ", 6) == 0) tm = tm + 6;
    tmlen = strcspn(tm, "[*");

    if (strncmp("VkClearColorValue", tm, tmlen) == 0 && strlen("VkClearColorValue") == tmlen) return true;
    if (strncmp("VkPipelineExecutableStatisticValueKHR", tm, tmlen) == 0 && strlen("VkPipelineExecutableStatisticValueKHR") == tmlen) return true;
    if (strncmp("VkClearValue", tm, tmlen) == 0 && strlen("VkClearValue") == tmlen) return true;
    if (strncmp("VkPerformanceValueDataINTEL", tm, tmlen) == 0 && strlen("VkPerformanceValueDataINTEL") == tmlen) return true;

    return false;
}

